CREATE DATABASE IF NOT EXISTS `swdle` DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci;
USE `swdle`;

-- Structure of table `skill`

CREATE TABLE IF NOT EXISTS `skill` (
    `id` int NOT NULL AUTO_INCREMENT,
    `com2us_id` int,
    `name` varchar(255),
    `description` TEXT,
    `slot` int,
    `cooltime` int,
    `hits` int,
    `aoe` BOOLEAN,
    `passive` BOOLEAN,
    `max_level` int,
    `icon_filename` varchar(255),
    PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- Structure of table `Family`

CREATE TABLE IF NOT EXISTS `family` (
    `id` int NOT NULL AUTO_INCREMENT,
    `name` varchar(255),
    PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- Structure of table `monster`

CREATE TABLE IF NOT EXISTS `monster` (
    `id` int NOT NULL AUTO_INCREMENT,
    `name` varchar(255),
    `com2us_id` int,
    `family_id` int,
    `image_filename` varchar(255),
    `element` varchar(255),
    `archetype` varchar(255),
    `base_stars` int,
    `natural_stars` int,
    `awaken_level` int,
    `skill_ups_to_max` int,
    `leader_skill` BOOLEAN,
    `homunculus` BOOLEAN,
    `farmable` BOOLEAN,
    `fusion_food` BOOLEAN,
    PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- Structure of table `monster_skill`

CREATE TABLE IF NOT EXISTS `monster_skill` (
    `id` int NOT NULL AUTO_INCREMENT,
    `monster_id` int,
    `skill_id` int,
    `slot` int,
    PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- Structure of table `history`

CREATE TABLE IF NOT EXISTS `history` (
    `id` int NOT NULL AUTO_INCREMENT,
    `date` datetime,
    `monster_daily_pick_id` int,
    `monster_daily_pick_indice_skill` int,
    `skill_daily_pick_id` int,
    `monster_image_daily_pick_id` int,
    PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- Structure of table `user`

CREATE TABLE IF NOT EXISTS `user` (
    `id` int NOT NULL AUTO_INCREMENT,
    `created_at` datetime DEFAULT CURRENT_TIMESTAMP,
    `updated_at` datetime DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    `username` varchar(255),
    `password` varchar(255),
    `email` varchar(255),
    `role_id` int DEFAULT 2,
    PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- Structure of table `role` 

CREATE TABLE IF NOT EXISTS `role` (
    `id` int NOT NULL AUTO_INCREMENT,
    `name` varchar(255),
    PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- Structure of table `buff` 

CREATE TABLE IF NOT EXISTS `buff` (
    `id` int NOT NULL AUTO_INCREMENT,
    `name` varchar(255),
    `image_filename` varchar(255),
    PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- Structure of table `monster_buff` 

CREATE TABLE IF NOT EXISTS `monster_buff` (
    `id` int NOT NULL AUTO_INCREMENT,
    `monster_id` int,
    `buff_id` int,
    PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- Structure of table `debuff` 

CREATE TABLE IF NOT EXISTS `debuff` (
    `id` int NOT NULL AUTO_INCREMENT,
    `name` varchar(255),
    `image_filename` varchar(255),
    PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- Structure of table `monster_debuff`

CREATE TABLE IF NOT EXISTS `monster_debuff` (
    `id` int NOT NULL AUTO_INCREMENT,
    `monster_id` int,
    `debuff_id` int,
    PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

